function PlacemarkShowPanorama(bU,jb,aT,aP,dz,hU){var ad=jb;var ak=bU;var fb=ad.zhbdmPlacemarkOriginalPosition;var bA=""+'<div id="contentStringStreetView'+hU+'" style="width:'+aT+'px;height:'+aP+'px;">'+'</div>';ad.close();ad.setContent(bA);ad.open(ak);var ft=new google.maps.StreetViewPanorama(document.getElementById("contentStringStreetView"+hU),{navigationControl:true,enableCloseButton:false,addressControl:false,linksControl:false});ft.setPosition(fb);if(dz!=""){ft.setPov(dz);}var cg=google.maps.event.addListener(ad,'domready',function(){google.maps.event.trigger(ft,'resize');google.maps.event.removeListener(cg);});};function Convert_Latitude_Decimal2DMS(cL){var fd;var eT;var eP="°";var fK="'";var fp="\"";var fA="";if((typeof cL!=='undefined')&&cL!=""&&cL!=null){if(cL=="-"){eT='S';fd=cL.substr(1,cL.length-1);}else{eT='N';fd=cL;}var eC,eJ,ek,dV,fc;dV=fd.toString().split(".");eC=dV[0];fc=dV[1];if((typeof fc!=='undefined')&&(fc!=null)){}else{fc="0";}fc=("0."+fc)*60;dV=fc.toString().split(".");eJ=dV[0];fc=dV[1];if((typeof fc!=='undefined')&&(fc!=null)){}else{fc="0";}fc=("0."+fc)*60;ek=fc.toFixed(1);fA=eC+eP+""+eJ+fK+""+ek+fp+""+eT;}return fA;};function Convert_Longitude_Decimal2DMS(cL){var fd;var eT;var eP="°";var fK="'";var fp="\"";var fA="";if((typeof cL!=='undefined')&&cL!=""&&cL!=null){if(cL=="-"){eT='W';fd=cL.toString().substr(1,cL.length-1);}else{eT='E';fd=cL;}var eC,eJ,ek,dV,fc;dV=fd.toString().split(".");eC=dV[0];fc=dV[1];if((typeof fc!=='undefined')&&(fc!=null)){}else{fc="0";}fc=("0."+fc)*60;dV=fc.toString().split(".");eJ=dV[0];fc=dV[1];if((typeof fc!=='undefined')&&(fc!=null)){}else{fc="0";}fc=("0."+fc)*60;ek=fc.toFixed(1);fA=eC+eP+""+eJ+fK+""+ek+fp+""+eT;}return fA;};function Map_Animate_Marker(bU,fR){var bd;if(bU.zhbdmMapSelectedMarkerFlg!="0"){bd=bU.zhbdmMapSelectedMarker;bd.setAnimation(null);}bU.zhbdmMapSelectedMarker=fR;bU.zhbdmMapSelectedMarkerFlg="1";fR.setAnimation(BMAP_ANIMATION_BOUNCE);};function Map_Animate_Marker_Hide(bU,fR){var bd;if(bU.zhbdmMapSelectedMarkerFlg!="0"&&bU.zhbdmMapSelectedMarker!=fR){bd=bU.zhbdmMapSelectedMarker;bd.setAnimation(null);bU.zhbdmMapSelectedMarkerFlg="0";bU.zhbdmMapSelectedMarker=null;}};function Map_Animate_Marker_Hide_Force(bU){var bd;if(bU.zhbdmMapSelectedMarkerFlg!="0"){bd=bU.zhbdmMapSelectedMarker;bd.setAnimation(null);bU.zhbdmMapSelectedMarkerFlg="0";bU.zhbdmMapSelectedMarker=null;}};function Map_Initialize_All(bU){bU.zhbdmMapSelectedMarkerFlg="0";bU.zhbdmMapSelectedMarker=null;};function Hover_Anchor(gW,fR,ce){var fM=new google.maps.MVCObject();if(gW=="placemark"){fM.set("position",fR.getPosition());}else if(gW=="path"){fM.set("position",ce.point);}else{}return fM;};function do_sleep(hS){var ix=new Date().getTime();for(var jw=0;jw<1e7;jw++){if((new Date().getTime()-ix)>hS){break;}}} 